import React, { useState }  from 'react';
import styles               from '../TagsInput/TagsInput.scss';

export const TagsInput = props => {

    const addTags = event => {
        if (event.key === "Enter" && event.target.value !== "") {
            props.setTags([...props.tags, event.target.value]);
            props.selectedTags([...props.tags, event.target.value]);
            event.target.value = "";
        }
    };

    const removeTags = index => {
        // setTags([...tags.filter(tag => tags.indexOf(tag) !== index)]);
        props.setTags([...props.tags.filter(tag => props.tags.indexOf(tag) !== index)]);
    };
    
    return (
        <div id="tags-input">
            <input type="text" onKeyUp={event => addTags(event)} placeholder="Press enter to add tags" />
            
            <div id="tags">
                {props.tags.map((tag, index) => (
                    <li className="tag" key={index}>
                        <span className="tag-title"> {tag} </span>
                        <i className="tag-close-icon" onClick={() => removeTags(index)} > x </i>
                    </li>
                ))}
            </div>
        </div>
    );
};
