import React, { useState, useEffect } from 'react';
import        { Button }              from '../../../shared/Button/Button';

export default function CreateEditQuestion() {

  const [field, setField] = useState("");
  const [type, setType] = useState("");
  const [questionText, setQuestionText] = useState("");
  const [textBelow, setTextBelow] = useState("");

  const [possibleAnswers, setPossibleAnswers] = useState("");
  const [answersLayout, setAnswersLayout] = useState("");
  const handleAnswerClick = (id) => {
    console.log(id);
    document.getElementById(id).checked = !document.getElementById(id).checked;
  }

  const [verticalChecked, setVerticalChecked] = useState(true);
  const handleLayoutClick = () => setVerticalChecked(!verticalChecked);

  const [tags, setTags] = useState('');

  useEffect(() => {
    document.title = "Login";
  }, [])

  return (
    <div className="create_edit_question">
      <h1> ({`${true}`} if create ? create : edit  {`<<---- change`}) Question </h1>
      <form className="new_question__form">
        <div className="content__section">

          {/* Might not work, JUST FOR NOW !!! */}
          <select id="question_type">
            <option value={type} onChange={(e) => setType (e.target.value)} > Single Answer Question    </option>
            <option value={type} onChange={(e) => setType (e.target.value)} > Multiple Answer Question  </option>
          </select>
          <input type="text" placeholder="Enter your question here" value={questionText}  onChange={(e) => setQuestionText  (e.target.value)} />
          <input type="text" placeholder="Type something"           value={textBelow}     onChange={(e) => setTextBelow     (e.target.value)} />
        </div>


        {/* NEED TO THINK OF A WAY TO MAKE THE ANSWERS PART DYMANIC */}

        <div className="content__section">
          <div className="answer__container">
            <button onClick={removeAnswer()}> X </button>
            <input id="answer_1" type='text' placeholder='First answer'   value={questionText}  onChange={(e) => setQuestionText(e.target.value)} />
            <input id="radio_1"  type="radio"    value="" onClick={(e) => handleAnswerClick(e.target.id)}   />    <label for="answer_1"   >  Incorrect  </label>
          </div>
          <div className="answer__container">
            <button onClick={removeAnswer()}> X </button>
            <input id="answer_2" type='text' placeholder='Second answer' value={questionText}  onChange={(e) => setQuestionText(e.target.value)} />
            <input id="radio_2"  type="radio"    value="" onClick={(e) => handleAnswerClick(e.target.id)}   />    <label for="answer_2"   >  Incorrect  </label>
          </div>
          <div className="answer__container">
            <button onClick={removeAnswer()}> X </button>
            <input id="answer_3" type='text' placeholder='Third answer'   value={questionText}  onChange={(e) => setQuestionText(e.target.value)} />
            <input id="radio_3"  type="radio"    value="" onClick={(e) => handleAnswerClick(e.target.id)}   />    <label for="answer_3"   >  Incorrect  </label>
          </div>
          <div className="answer_layout__container">
            <label name="layout__label"> Answers layout: </label>
            <input id="vertical"    type="radio"  name="answer_layout" onClick={handleLayoutClick} checked={verticalChecked}   />    <label for="vertical"   >  Vertical   </label>
            <input id="horizontal"  type="radio"  name="answer_layout" onClick={handleLayoutClick} checked={!verticalChecked}  />    <label for="horizontal" >  Horizontal </label>
            <button onClick={Add_Answer()}> Add an Answer </button>
          </div>
        </div>

      </form>
    </div>
  )
};

function something(id) {
  console.log(id);
}

function Add_Answer() {
