import React, { useEffect, useState }   from 'react';
import        { useHistory }            from 'react-router-dom';
import styles                           from './Reports.css';

export const Reports = ({ type }) => {

    // ------------------------ Fetch Info Hooks -----------------------

    const [ tests,       setTests ]             = useState(null);

    // ------------------------- Content Hooks -------------------------

    const [ dateFrom,    setDateFrom    ]       = useState("");
    const [ dateTo,      setDateTo      ]       = useState("");
    const [ dateRange,   setDateRange   ]       = useState({dateFrom} - {dateTo})
    const [ past,        setPast        ]       = useState(true);

    const history = useHistory();
    const back = () => {
        history.goBack();
    }

    const handleCheckboxChange = (checked) => {
        setPast(checked);
    }

    const fetchTests = () => {
        fetch("http://localhost:8000/tests", {
          method: 'GET',
        })
        .then((res) => res.json())
        .then((data) => { 
          setTests(data); 
        })
        .catch((err) => console.log('error fetching tests:' + err))
    }

    useEffect(() => {
        document.title = "Reports";
        fetchTests();
    }, [])

    return (
        <div className="reports noselect">
            <div id="headers__container">
                <h1> Test Report for {type ? type : ""} </h1>
            </div>
            <div id="content__container">
                <form id="report__form">
                    <table id="content__table">
                        <tbody>
                            <tr>
                                <td>
                                    <label id="label__test_name">       Select Test:            </label>
                                </td>

                                <td> <select id="question_type__select" value={type}>
                                     {tests && tests.map((test) => (
                                        <option key={test.id} value={test.testName}> {test.testName} </option>
                                     ))}
                                     </select> </td>
                            </tr>
                            <tr>
                                <td>
                                    <label id="label__date_range">      Date Range:             </label>
                                </td>
                                <td>
                                    <label id="label__from">            From:                   </label>
                                    <input type="date" value={dateFrom} onChange={ (e) => dateFrom === false ?   setDateFrom("") :  setDateFrom(dateFrom) }  disabled={past} />
                                </td>
                                <td>
                                    <label id="label__to">              To:                     </label>
                                    <input type="date" value={dateTo}   onChange={ (e) => dateTo === false ?     setDateTo(false) : setDateTo(dateTo) }      disabled={past} />
                                </td>
                            </tr>
                            <tr>
                                <td>  </td>
                                <td>
                                    <label id="label__OR">              OR                      </label>
                                </td>
                            </tr>

                            <tr>
                                <td>  </td>
                                <td>
                                    <input type="checkbox" checked={past} onChange={ (e) => handleCheckboxChange(e.target.checked) } />
                                    <label id="label__past">                Any date in the past        </label>
                                </td>
                            </tr>
                        </tbody>
                    </table>
                </form>
                <div id="button__container">
                <button className="regular__button" onClick={ Back() }>     {`<<` } Back                </button>
                <button className="regular__button" type="submit">          Generate Report             </button>
                </div>
            </div>

        </div>
    )
};

