import React, { useEffect } from 'react';
// import { Collapsible } from '../../../shared/Collapsible/Collapsible';
import Accordion from '../../../shared/Accordion/Accordion';

export default function MainPage() {

    useEffect(() => {
        document.title = 'Main Page';
    }, []);

//     let coll = document.getElementsByClassName("collapsible");
//     let i;

//     for (i = 0; i < coll.length; i++) {
//   coll[i].addEventListener("click", function() {
//     this.classList.toggle("active");
//     let content = this.nextElementSibling;

//     if (content.style.maxHeight) {
//       content.style.maxHeight = null;
//     } else {
//       content.style.maxHeight = content.scrollHeight + "px";
//     } 
//   });
// }

    const accordionData = [
        {
        title: 'Section 1',
        content: `Lorem ipsum dolor, sit amet consectetur adipisicing elit. Quis sapiente
        laborum cupiditate possimus labore, hic temporibus velit dicta earum
        suscipit commodi eum enim atque at? Et perspiciatis dolore iure
        voluptatem.`
        },
        {
        title: 'Section 2',
        content: `Lorem ipsum, dolor sit amet consectetur adipisicing elit. Mollitia veniam
        reprehenderit nam assumenda voluptatem ut. Ipsum eius dicta, officiis
        quaerat iure quos dolorum accusantium ducimus in illum vero commodi
        pariatur? Impedit autem esse nostrum quasi, fugiat a aut error cumque
        quidem maiores doloremque est numquam praesentium eos voluptatem amet!
        Repudiandae, mollitia id reprehenderit a ab odit!`
        },
        {
        title: 'Section 3',
        content: `Sapiente expedita hic obcaecati, laboriosam similique omnis architecto ducimus magnam accusantium corrupti
        quam sint dolore pariatur perspiciatis, necessitatibus rem vel dignissimos
        dolor ut sequi minus iste? Quas?`
        }
    ];

    return (
        <div id='container'>
            <div id="select__container">
                <label> Choose a field of study:  </label>
                <select id='field'>
                    <option value='development'> Development </option>
                </select>
            </div>
            <div id='collapsible__container'>
                {/* <Collapsible name='Manage Questions' content='something'></Collapsible>
                <Collapsible name='Manage Exams' content='something'></Collapsible>
                <Collapsible name='Manage Reports' content='something'></Collapsible> */}

                <div className="accordion">
                    {accordionData.map(({ title, content }) => (
                        <Accordion title={title} content={content} />
                    ))}
                </div>

            </div>
        </div>
    )
};
