import React, { useState, useEffect } from 'react';
import        { useHistory          } from 'react-router-dom';
import styles                         from './SignUp.css';

export default function SignUp() {

  const [ name,             setName           ] = useState("");
  const [ email,            setEmail          ] = useState("");
  const [ password,         setPassword       ] = useState("");

  const [ allBeenFilled,    setAllBeenFilled  ] = useState(false);

  const history = useHistory();

  const userRegister = () => {

    if (allBeenAnswered) {
        calcGrade();

        fetch('http://localhost:8000/studentTests', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                id: studentTests[studentTests.length - 1].stID + 1,
                testID: test.testID,
                studentID: studentID,
                studentName: fullName,
                grade: grade,
                questions: studentQuestions,
                submitted: Date.now()
            }),
        })
            .then((res) => res.json())
            .then((data) => {
                console.log(data);
                return true;
            })
            .catch((err) => {
                console.log(`error ${err}`)
                return false;
            });
    }

    return false;
  }

  const handleSubmit = (event) => {
    console.log("handle submit");
    event.preventDefault();

    if (userRegister()) {
      history.push("/");
    } else {
      console.log("login failed - show kind of message");
    }
  }

  useEffect(() => {
      document.title = "Sign up";
  }, [])

  useEffect(() => {
    
}, [name, email, password])

  return (
    <div className="sign_up noselect">
      <h1 className="page__header"> Sign-up </h1>
      <form className="sign_up__form">
        <div className="form_content__container">
          <input type="name"      placeholder="Name"      value={name}      onChange={(e) => setName(e.target.value)}     />
          <input type="email"     placeholder="Email"     value={email}     onChange={(e) => setEmail(e.target.value)}    />
          <input type="password"  placeholder="Password"  value={password}  onChange={(e) => setPassword(e.target.value)} />
          <button id="submit__button" type="submit" onClick={handleSubmit}> Submit </button>
        </div>
      </form>
    </div>
  )
};
