import React, { useEffect, useState }       from 'react';
import        { SummaryTable }              from '../../../Admin/SummaryTable/SummaryTable';
import        { useHistory, useLocation }   from 'react-router-dom';
import styles                               from './TestReport.css'

export const TestReport = () => {

    // ----------------------------- Fetch Info Hooks ----------------------------

    const [ averageGrade,        setAverageGrade        ]       = useState(0);
    
    // ------------------------------ Summary Hooks ------------------------------
    
    const [ test,                setTest                ]       = useState(null);
    const [ field,               setField               ]       = useState("");
    const [ dateRange,           setDateRange           ]       = useState("");
    const [ passingGrade,        setPassingGrade        ]       = useState(0);
    const [ questions,           setQuestions           ]       = useState([]);
    // const [ numOfQuestions,      setNumOfQuestions      ]       = useState(0);
    
    // ------------------------------ Grades Hooks -------------------------------
    
    const [ respondentChosen,    setRespondentChosen    ]       = useState(false);
    
    // ---------------------------- Statistics Hooks -----------------------------

    const [ filterContent,       setFilterContent       ]       = useState("");

    // ---------------------------------------------------------------------------

    const location = useLocation(); // for location state

    const history = useHistory();
    const back = () => {
        history.goBack();
    }

    const expandAll = () => {

    }
    
    const exportToExecl = () => {

    }

    const printReport = () => {

    }

    const countNumberOfQuestions = () => {
        let counter = 0;
        if (questions) {
            questions.map(() => counter++);
        }

        return counter;
    }
    
    useEffect(() => {
        document.title = "Test Report";
    }, [])

    useEffect(() => {
        let isMounted = true;           // note mutable flag
    
        if (isMounted) {                // add conditional check

        }

        console.log(location.state.field)  // for location state
        console.log(location.state.test)  // for location state
        console.log(location.state.dateRange) 

        setField(location.state.field);
        setTest(location.state.test)
        setDateRange(location.state.dateRange);

        countNumberOfQuestions();
    }, [])

    return (
        <div className="test_report noselect">
            <div id="headers__container">
                <h1> Test result for {field} </h1>
            </div>

            <div id="content__container">
                <div id="Summary_table__container">                    
                <table id="questions__table">
                    <tbody>
                        <tr className="space_under">
                            <th> Summary </th>
                        </tr>

                        {test && Object.keys(test).map((p) => {
                            return (
                                <tr className="space_under">
                                    <td><label key={p.testName}>    {p.testName}   </label></td>
                                    <td><label key={p.testID}>      {p.testID}     </label></td>
                                    <td><label key={p.testCode}>    {p.testCode} </label></td>
                                    <td><label key={p.testType}>    {p.testType} </label></td>
                                    <td><label key={p.questions}>   {countNumberOfQuestions()} </label></td>
                                    <td><label key={p.testName}>    {p.testName} </label></td>
                                    <td><label key={p.testName}>    {p.testName} </label></td>
                                    <td><label key={p.testName}>    {p.testName} </label></td>
                                </tr> 
                            )
                        })}
                    </tbody>
                </table>
                </div>

                <button onClick={ () => expandAll() }> Expand All </button>
                <table id="Details__table">
                    <tbody>
                        <tr className="header_row space_under border_bottom">
                            <th>        ID                     </th>
                            <th>        Question               </th>
                            <th>        Answered Correctly?    </th>
                            <th>        Date Answered          </th>
                        </tr>
                        {/* {{AdminMenu.map(({ title, content }) => (                              <<-------------------    EXAMPLE
                                        <Accordion title={title} content={content} />
                                    ))}}
                        <tr >                                                                 <<-------------------    EXAMPLE 2  
                            { {children.map(({ ID, Link, name, update, type, version }) => (
                                
                            <td> {ID} </td>
                            <td> {Link} </td>
                            <td> {name} </td>
                            <td> {update} </td>
                            <td> {type} </td>
                            <td> {version} </td>
                            <td> </td>
                            ))}}
                        </tr> */}
                    </tbody>
                </table>

                <div id="buttons__container">
                    <button className="regular__button" onClick={ () =>   back()           }>  {`<<` } Back    </button>
                    <button className="regular__button" onClick={ () =>   exportToExecl()  }>  Export To Excel </button>
                    <button className="regular__button" onClick={ () =>   printReport()    }>  Print Report    </button>
                </div>
            </div>
        </div>
    )
};
