import styles                   from './Filter.css';

export const Filter = ({ questions, filterByTags, filterStatus, totalAmount, filteredAmount, handleFilterByChange, setFilter, setFilteredQuestions }) => {

    const handleFilterContentChange = (value) => {
        
        if (questions && filterByTags) {
            if (value === "") setFilter(false);
            else setFilter(true);
    
            let newQuestionList = questions;
            setFilteredQuestions(newQuestionList.filter((question) => { 
                
                let contains = false;
    
                if (filterByTags === true) {
                    question.tags.map((tag) => {
                        if (tag.toLowerCase().includes(value)) contains = true;
                    });
                } else {
                    if (question.title.toLowerCase().includes(value)) contains = true;
                }
    
                if (contains === true) return question;
                else return null;
    
            }));
        }
        }

    return (
        <div id="filter__container">
            <div id="filter_by__container">
                <label> Filter by: </label>
                <select onChange={ (e) => handleFilterByChange(e.target.value) }>
                    <option value="tags">       tags       </option>
                    <option value="content">    content    </option>
                </select>
            </div>

            <div id="filter_input__container">
                <input id="filter__input" type="text" onChange={ (e) => handleFilterContentChange(e.target.value) } placeholder="Enter a list of keywords separated by commas"/>
            </div>

            <div id="filter_status__container">
                <label id="filter__status">         Filter is   {filterStatus === false ? "OFF" : "ON"}                                                           </label>
            </div>

            <div id="amount_filtered__container">
                <label id="amount__filtered">       Filtered    {filteredAmount ? filteredAmount : ""} of total {totalAmount ? totalAmount : ""}    </label>
            </div>
        </div>
    )
};
