import React, { useEffect, useState }         from    'react';
import { AdminMainPageMenu }        from    '../../../../utils/content';
import { Link }                     from    'react-router-dom';
import styles                       from    './MainPage.css';

export default function MainPage() {

    const [ fields,     setFields       ] = useState(null);
    const [ field,      setField        ] = useState(null);

    // const toLowerCase = (title) => {
    //     const lowerCase = title.toLowerCase();
    //     setFieldTitle(lowerCase);
    //     console.log(lowerCase);
    //   };

    useEffect(() => {
        document.title = 'Admin panel';
        let isMounted = true;               // note mutable flag

        fetch("http://localhost:8000/fields")
        .then(res => {
            return res.json();
        })
        .then((data) => {
            if (isMounted) {                // add conditional check 
                //    console.log(data); 
                   setFields(data);
                   setField(data[0]);
                   console.log(data[0]);
            }
        });

    }, []);

    return (
        <div className="admin_mainpage noselect">
            <div id="main__container">
                <div id="header__container">
                    <h1> Administrator panel </h1>
                </div>
                <div id="content__container">
                    <table id="content__table">
                        <tbody>
                            <tr id="select__tr">
                                <td id="select__td">
                                    <label id="label__choose"> Choose a field of study: </label>
                                    <select id="fields__select" defaultValue={field} onChange={() => setField(field)}>
                                        { fields && fields.map((field) => (
                                            <option key={field.id}> {field.title} </option>
                                        ))}
                                    </select>
                                </td>
                            </tr>

                            <Router>
                                <Navbar/>
                                <Switch>

                                    {/* Shared */}
                                    <Route path='/' exact                   component={Home}                    />
                                    <Route path='/home'                     component={Home}                    />
                                    <Route path='/login'                    component={Login}                   />
                                    <Route path='/sign-up'                  component={SignUp}                  />
                                    <Route path='/forgot-password'          component={ForgotPassword}          />
                                    <Route path='/about'                    component={About}                   />
                                    <Route path='/error404'                 component={Error404}                />

                                    {/* Admin */}
                                    <Route path='/admin/main-page'          component={MainPage}                />
                                    <Route path='/admin/create-question'    component={CreateEditQuestion}      />
                                    <Route path='/admin/edit-question'      component={CreateEditQuestion}      />
                                    <Route path='/admin/create-test'        component={CreateEditTest}          />
                                    <Route path='/admin/edit-test'          component={CreateEditTest}          />
                                    <Route path='/admin/manage-questions'   component={ManageQuestions}         />
                                    <Route path='/admin/manage-tests'       component={ManageTests}             />
                                    <Route path='/admin/reports'            component={Reports}                 />
                                    <Route path='/admin/report-by-name'     component={ReportByRespondentName}  />
                                    {/* <Route path='/admin/test-report'    component={TestReport} />       ROUTE FOR THE TEST REPORT ITSELF */}
                                    
                                </Switch>
                            </Router>

                            { AdminMainPageMenu.map((child) => (
                            <tr key={child.title}>
                                <td> <Link className="menu_item" to={child.link} field={field}> {child.title} </Link> </td>
                            </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
};
