import React, { useEffect, useState         }   from 'react';
import        { useHistory, useLocation     }   from 'react-router-dom';
import { QuestionTable } from '../../../Admin/QuestionTable/QuestionTable';

export default function Test() {

    const [ studentTests,       setStudentTests     ] = useState(null);
    const [ questions,          setQuestions        ] = useState(null);
    const [ questionTypes,      setQuestionTypes    ] = useState(null);
    
    const [ test,               setTest             ] = useState("");
    const [ studentID,          setStudentID        ] = useState("");
    const [ fullName,           setFullName         ] = useState("");
    const [ email,              setEmail            ] = useState("");
    
    const [ grade,              setGrade            ] = useState("");
    const [ status,             setStatus           ] = useState("");
    const [ summary,            setSummary          ] = useState("");

    const [ currentQuestion,    setCurrentQuestion  ] = useState(null);
    const [ currentIndex,       setCurrentIndex     ] = useState(0);
    const [ studentQuestions,   setStudentQuestions ] = useState([]);

    const location = useLocation();
    const history = useHistory();

    const calcGrade = () => {
        let grade = 0;
        let numCorrectly = 0;

        questions.map((question) => {
            studentQuestions.map((studentQuestion) => {
                if (question.questionID === studentQuestion.questionID) {

                    question.answers.map((answer) => {
                        studentQuestion.answers.map((studentAnswer) => {
                            if (answer.answerID === studentAnswer.answerID && answer.answerTitle === studentAnswer.answerTitle) {

                                console.log("answer is correct");
                                numCorrectly++;
                            }
                        })
                    })
                }
            })
        })

        grade = Math.round((questions.length / numCorrectly) * 100);
        setGrade(grade);

        if (grade >= test.passingGrade) setStatus("Passed");
        else setStatus("Failed");

        if (numCorrectly > 0) setSummary(`You answered ${numCorrectly} questions correctly, out of ${questions.length} questions in total.`);
        else setSummary(`Out of ${questions.length} questions, You answered ${numCorrectly} correctly..`);
    }

    const handleQuestions = (value) => {
        console.log(currentIndex);
        let lowerCase = value.toString().toLowerCase();
        if (lowerCase.includes("next")) {
            if (currentIndex < questions.length - 1) setCurrentIndex(currentIndex + 1);
        }

        else if (lowerCase.includes("previous")) {
            if (currentIndex > 0) setCurrentIndex(currentIndex - 1);
        }
    }

    const handleSubmit = (event) => {
        console.log("handle submit");
        event.preventDefault();

        if (submitTest()) {

            history.push({
                pathname: `/student/review-test`,
                // search: '?update=true',  // query string
                state: {  // location state
                    // update: true, 
                    student: {
                        studentID: studentID,
                        fullName: fullName,
                        email: email
                    },
                    grade: grade,
                    status: status,
                    summary: summary,
                    passingGrade: test.passingGrade,
                    questions: studentQuestions
                },
            });
        }

    }

    const submitTest = () => {
        console.log(test.testID);

        calcGrade();

        fetch('http://localhost:8000/studentTests', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                id: studentTests[studentTests.length - 1].stID + 1,
                testID: test.testID,
                studentID: studentID,
                studentName: fullName,
                grade: grade,
                questions: studentQuestions,
                submitted: Date.now()
            }),
        })
            .then((res) => res.json())
            .then((data) => {
                console.log(data);
                return true;
            })
            .catch((err) => {
                console.log(`error ${err}`)
                return false;
            });
    }

    const handleAnswerClick = (id) => {
        console.log("handle answer click");
        // let newAnswers = answers;
        // let amountCorrect = newAnswers.filter((answer) => answer.correct === true)
        // newAnswers.filter((answer) => answer.answerID === id).map((answer) => {
    
        //   if (amountCorrect.length <= 1) {
        //     if (answer.correct === false) answer.correct = true;
        //   }
    
        //   else if (amountCorrect.length > 1 && amountCorrect.length < newAnswers.length - 1) {
        //     answer.correct = !answer.correct;
        //     console.log(`answer_${id} state - ${answer.correct}`);
        //   }
    
        //   else {
        //     if (answer.correct === true) answer.correct = false;
        //   }
    
        //   return answer;
        // });
        // setAnswers(newAnswers);
        // handlePossibleAnswersChange();
      }

    const fetchQuestions = () => {
        fetch("http://localhost:8000/questions", {
            method: 'GET',
        })
            .then((res) => res.json())
            .then((data) => {
                setQuestions(data);
            })
            .catch((err) => console.log('error fetching questions:' + err))
    }

    const fetchQuestionTypes = () => {
        fetch("http://localhost:8000/questionTypes", {
          method: 'GET',
        })
        .then((res) => res.json())
        .then((data) => { 
          setQuestionTypes(data);
        })
        .catch((err) => console.log('error fetching question types:' + err));
      }

    // Meant for fetching the necessary information on first render
    useEffect(() => {
        document.title = "State Test";

        if (location.state) {
            if (location.state.test) {
                setTest(location.state.test);

                if (location.state.test.questions) {
                    let questionArray = [];

                    if (questions) {
                        location.state.test.questions.map((testQuestion) => {
                            questions.map((question) => {
                                if (question.questionID === testQuestion.questionID) {
                                    questionArray.push({
                                        "questionID": question.questionID,
                                        "questionTitle": question.questionTitle,
                                        "questionType": question.questionType,
                                        "questionAnswers": [ question.questionAnswers.map((answer) => {
                                            return (
                                                JSON.stringify({
                                                    "answerID": answer.answerID,
                                                    "answerTitle": answer.answerTitle
                                                })
                                            )
                                        }) ],
                                        "chosenAnswers": []
                                    });
                                }
                            })
                        })
                    }

                    setStudentQuestions(questionArray);
                    setCurrentQuestion(questionArray[0]);
                }
            }

            if (location.state.student) {
                setStudentID(location.state.studentID);
                setFullName(location.state.fullName);
                setEmail(location.state.email);
            }
        }
    }, [questions])


    useEffect(() => {
        let isMounted = true;           // note mutable flag

        if (isMounted) {                // add conditional check 
            fetchQuestions();
            fetchQuestionTypes();
        }

        return () => { isMounted = false }; // cleanup toggles value, if unmounted
    }, [])

    return (
        <div className="test noselect">
            <label> Please complete the following form to begin: </label>
            <form id="test__form" onSubmit={handleSubmit}>
                <div id="test__container">
                    <div id="test_questions__container">

                        { currentQuestion && <>                        
                            <h1 key={currentQuestion.questionID}> {currentQuestion.questionTitle} </h1>

                            { currentQuestion.questionAnswers && currentQuestion.questionAnswers.map((answer) => {
                                console.log(currentQuestion.questionAnswers)
                                return (
                                    <>
                                        <label> {answer.answerTitle} </label>
                                        <input id={`radio_${answer.answerID}`} name={currentQuestion.questionType ? (currentQuestion.questionType === questionTypes[1] ? currentQuestion.questionType : "") : ""} type="radio" onClick={(e) => handleAnswerClick(answer.answerID)} /> 
                                    </> )
                            })}
                        </> }

                        {/* { questionTypes && studentQuestions && studentQuestions.map((question) => {
                            return (
                                <>
                                    <h1 key={question.questionID}> {question.questionTitle} </h1>

                                    { question.questionAnswers.map((answer) => {
                                        return (
                                            <>
                                                <label> {answer.answerTitle} </label>
                                                <input id={`radio_${answer.answerID}`} name={question.questionType ? (question.questionType === questionTypes[1] ? question.questionType : "") : ""} type="radio" onClick={(e) => handleAnswerClick(answer.answerID)} /> 
                                            </> )
                                    })}
                                </> )
                        })} */}
                    </div>

                    <div id="test_buttons__container">
                        <button id="previous_question"  type="button" onClick={handleQuestions}> {`<<`} Previous Question </button>
                        <button id="next_question"      type="button" onClick={handleQuestions}> Next Question {`>>`}</button>
                        { questions  && currentIndex === questions.length - 1 && <button id="submit__button"     type="submit"> Submit </button> }
                    </div>
                </div>
            </form>
        </div>
    )
};
